      return;
    }

    try {
      const updatedMed = await api.addMedicamento(selectedPatient.id, newMed);
      const updatedPatient = {
        ...selectedPatient,
        medicamentos: [...(selectedPatient.medicamentos || []), updatedMed]
      };
      setSelectedPatient(updatedPatient);
      setPatients(patients.map(p => p.id === selectedPatient.id ? updatedPatient : p));
      setNewMed({ nombre: '', dosis: '', frecuencia: '' });
    } catch (error) {
      console.error('Error al agregar medicamento:', error);
      alert('Error al agregar medicamento');
    }
