const API_URL = 'http://localhost:3001/api';

export const api = {
  // Pacientes
  getPatients: async () => {
    const response = await fetch(`${API_URL}/patients`);
    return response.json();
  },

  addPatient: async (patient) => {
    const response = await fetch(`${API_URL}/patients`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(patient),
    });
    return response.json();
  },

  updatePatient: async (id, patient) => {
    const response = await fetch(`${API_URL}/patients/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(patient),
    });
    return response.json();
  },

  deletePatient: async (id) => {
    const response = await fetch(`${API_URL}/patients/${id}`, {
      method: 'DELETE',
    });
    return response.json();
  },

  // Medicamentos
  addMedicamento: async (patientId, medicamento) => {
    const response = await fetch(`${API_URL}/patients/${patientId}/medicamentos`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(medicamento),
    });
    return response.json();
  },

  // Alergias
  addAlergia: async (patientId, alergia) => {
    const response = await fetch(`${API_URL}/patients/${patientId}/alergias`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(alergia),
    });
    return response.json();
  },

  // Enfermedades
  addEnfermedad: async (patientId, enfermedad) => {
    const response = await fetch(`${API_URL}/patients/${patientId}/enfermedades`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(enfermedad),
    });
    return response.json();
  },

  // Patologías
  addPatologia: async (patientId, patologia) => {
    const response = await fetch(`${API_URL}/patients/${patientId}/patologias`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(patologia),
    });
    return response.json();
  },

  // Registros de entrada/salida
  addRegistro: async (patientId, registro) => {
    const response = await fetch(`${API_URL}/patients/${patientId}/registros`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(registro),
    });
    return response.json();
  },

  // Staff
  getStaff: async () => {
    const response = await fetch(`${API_URL}/staff`);
    return response.json();
  },

  addStaff: async (staff) => {
    const response = await fetch(`${API_URL}/staff`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(staff),
    });
    return response.json();
  },

  deleteStaff: async (id) => {
    const response = await fetch(`${API_URL}/staff/${id}`, {
      method: 'DELETE',
    });
    return response.json();
  },
};
