import React, { useState } from 'react';
import { Home, Users, FilePlus } from 'lucide-react';

// Componente principal
const AdminPanel = () => {
  const [currentView, setCurrentView] = useState('welcome');
  const [patients, setPatients] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);

  // Componente de Bienvenida
  const WelcomeView = () => (
    <div className="text-center p-8 bg-white rounded-lg shadow-md">
      <h1 className="text-3xl font-bold text-sky-700 mb-4">Bienvenidos al Sistema de Administración</h1>
      <h2 className="text-xl text-sky-600 mb-6">Hogar de Ancianos</h2>
      <p className="text-gray-600">
        Seleccione una opción del menú para comenzar a gestionar los pacientes y sus medicamentos.
      </p>
    </div>
  );

  // Formulario de Nuevo Paciente
  const NewPatientForm = () => {
    const [formData, setFormData] = useState({
      nombre: '',
      apellido: '',
      fechaNacimiento: '',
      rut: '',
      telefonoEmergencia: '',
      contactoEmergencia: '',
      genero: ''
    });

    const handleSubmit = (e) => {
      e.preventDefault();
      setPatients([...patients, { ...formData, id: Date.now(), medicamentos: [] }]);
      setFormData({
        nombre: '',
        apellido: '',
        fechaNacimiento: '',
        rut: '',
        telefonoEmergencia: '',
        contactoEmergencia: '',
        genero: ''
      });
    };

    return (
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-2xl font-bold text-sky-700 mb-6">Crear Nueva Ficha de Paciente</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-gray-700 mb-2">Nombre</label>
              <input
                type="text"
                className="w-full p-2 border rounded focus:border-sky-500"
                value={formData.nombre}
                onChange={(e) => setFormData({...formData, nombre: e.target.value})}
                required
              />
            </div>
            <div>
              <label className="block text-gray-700 mb-2">Apellido</label>
              <input
                type="text"
                className="w-full p-2 border rounded focus:border-sky-500"
                value={formData.apellido}
                onChange={(e) => setFormData({...formData, apellido: e.target.value})}
                required
              />
            </div>
            <div>
              <label className="block text-gray-700 mb-2">Fecha de Nacimiento</label>
              <input
                type="date"
                className="w-full p-2 border rounded focus:border-sky-500"
                value={formData.fechaNacimiento}
                onChange={(e) => setFormData({...formData, fechaNacimiento: e.target.value})}
                required
              />
            </div>
            <div>
              <label className="block text-gray-700 mb-2">RUT</label>
              <input
                type="text"
                className="w-full p-2 border rounded focus:border-sky-500"
                value={formData.rut}
                onChange={(e) => setFormData({...formData, rut: e.target.value})}
                required
              />
            </div>
            <div>
              <label className="block text-gray-700 mb-2">Teléfono de Emergencia</label>
              <input
                type="tel"
                className="w-full p-2 border rounded focus:border-sky-500"
                value={formData.telefonoEmergencia}
                onChange={(e) => setFormData({...formData, telefonoEmergencia: e.target.value})}
                required
              />
            </div>
            <div>
              <label className="block text-gray-700 mb-2">Contacto de Emergencia</label>
              <input
                type="text"
                className="w-full p-2 border rounded focus:border-sky-500"
                value={formData.contactoEmergencia}
                onChange={(e) => setFormData({...formData, contactoEmergencia: e.target.value})}
                required
              />
            </div>
            <div>
              <label className="block text-gray-700 mb-2">Género</label>
              <select
                className="w-full p-2 border rounded focus:border-sky-500"
                value={formData.genero}
                onChange={(e) => setFormData({...formData, genero: e.target.value})}
                required
              >
                <option value="">Seleccione...</option>
                <option value="masculino">Masculino</option>
                <option value="femenino">Femenino</option>
                <option value="otro">Otro</option>
              </select>
            </div>
          </div>
          <button
            type="submit"
            className="w-full bg-sky-600 text-white py-2 px-4 rounded hover:bg-sky-700 transition-colors"
          >
            Guardar Paciente
          </button>
        </form>
      </div>
    );
  };

  // Vista de Lista de Pacientes y Medicamentos
  const PatientListView = () => {
    const [newMed, setNewMed] = useState({ nombre: '', dosis: '', frecuencia: '' });

    const addMedicamento = (patientId) => {
      const updatedPatients = patients.map(patient => {
        if (patient.id === patientId) {
          return {
            ...patient,
            medicamentos: [...patient.medicamentos, { ...newMed, id: Date.now() }]
          };
        }
        return patient;
      });
      setPatients(updatedPatients);
      setNewMed({ nombre: '', dosis: '', frecuencia: '' });
    };

    const removeMedicamento = (patientId, medId) => {
      const updatedPatients = patients.map(patient => {
        if (patient.id === patientId) {
          return {
            ...patient,
            medicamentos: patient.medicamentos.filter(med => med.id !== medId)
          };
        }
        return patient;
      });
      setPatients(updatedPatients);
    };

    return (
      <div className="space-y-6">
        <h2 className="text-2xl font-bold text-sky-700">Fichas de Pacientes</h2>
        {patients.map(patient => (
          <div key={patient.id} className="bg-white p-6 rounded-lg shadow-md">
            <div className="grid grid-cols-2 gap-4 mb-4">
              <div>
                <h3 className="text-xl font-semibold text-sky-600">
                  {patient.nombre} {patient.apellido}
                </h3>
                <p className="text-gray-600">RUT: {patient.rut}</p>
                <p className="text-gray-600">Fecha de Nacimiento: {patient.fechaNacimiento}</p>
                <p className="text-gray-600">Género: {patient.genero}</p>
              </div>
              <div>
                <p className="text-gray-600">Contacto de Emergencia: {patient.contactoEmergencia}</p>
                <p className="text-gray-600">Teléfono: {patient.telefonoEmergencia}</p>
              </div>
            </div>

            <div className="border-t pt-4">
              <h4 className="text-lg font-semibold text-sky-600 mb-2">Medicamentos</h4>
              <div className="grid grid-cols-4 gap-2 mb-2">
                <input
                  placeholder="Nombre del medicamento"
                  className="p-2 border rounded"
                  value={newMed.nombre}
                  onChange={(e) => setNewMed({...newMed, nombre: e.target.value})}
                />
                <input
                  placeholder="Dosis"
                  className="p-2 border rounded"
                  value={newMed.dosis}
                  onChange={(e) => setNewMed({...newMed, dosis: e.target.value})}
                />
                <input
                  placeholder="Frecuencia"
                  className="p-2 border rounded"
                  value={newMed.frecuencia}
                  onChange={(e) => setNewMed({...newMed, frecuencia: e.target.value})}
                />
                <button
                  onClick={() => addMedicamento(patient.id)}
                  className="bg-sky-600 text-white p-2 rounded hover:bg-sky-700"
                >
                  Agregar Medicamento
                </button>
              </div>

              <div className="space-y-2">
                {patient.medicamentos.map(med => (
                  <div key={med.id} className="flex items-center justify-between bg-gray-50 p-2 rounded">
                    <span>{med.nombre} - {med.dosis} - {med.frecuencia}</span>
                    <button
                      onClick={() => removeMedicamento(patient.id, med.id)}
                      className="text-red-600 hover:text-red-800"
                    >
                      Eliminar
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-sky-50">
      {/* Navegación */}
      <nav className="bg-sky-600 text-white p-4">
        <div className="container mx-auto flex justify-between items-center">
          <h1 className="text-2xl font-bold">Panel de Administración</h1>
          <div className="flex space-x-4">
            <button
              onClick={() => setCurrentView('welcome')}
              className={`flex items-center space-x-2 p-2 rounded ${currentView === 'welcome' ? 'bg-sky-700' : 'hover:bg-sky-700'}`}
            >
              <Home size={20} />
              <span>Inicio</span>
            </button>
            <button
              onClick={() => setCurrentView('newPatient')}
              className={`flex items-center space-x-2 p-2 rounded ${currentView === 'newPatient' ? 'bg-sky-700' : 'hover:bg-sky-700'}`}
            >
              <FilePlus size={20} />
              <span>Nueva Ficha</span>
            </button>
            <button
              onClick={() => setCurrentView('patientList')}
              className={`flex items-center space-x-2 p-2 rounded ${currentView === 'patientList' ? 'bg-sky-700' : 'hover:bg-sky-700'}`}
            >
              <Users size={20} />
              <span>Ver Pacientes</span>
            </button>
          </div>
        </div>
      </nav>

      {/* Contenido Principal */}
      <main className="container mx-auto p-6">
        {currentView === 'welcome' && <WelcomeView />}
        {currentView === 'newPatient' && <NewPatientForm />}
        {currentView === 'patientList' && <PatientListView />}
      </main>
    </div>
  );
};

export default AdminPanel;
